namespace Ivory\LuceneSearchBundle\Tests\DependencyInjection;

use Symfony\Component\Config\FileLocator;
use Symfony\Component\DependencyInjection\ContainerBuilder;
use Symfony\Component\DependencyInjection\Loader\YamlFileLoader;

/**
 * Yaml Ivory Lucene search extension test.
 *
 * @author GeLo <geloen.eric@gmail.com>
 */
class YamlIvoryLuceneSearchExtensionTest extends AbstractIvoryLuceneSearchExtensionTest
{
    /**
     * {@inheritdoc}
     */
    protected function loadConfiguration(ContainerBuilder $container, $configuration)
    {
        $loader = new YamlFileLoader($container, new FileLocator(__DIR__.'/../Fixtures/config/Yaml/'));
        $loader->load($configuration.'.yml');
    }
}
